import React from "react";
import '../scss/main.scss';
import TinySlider from "tiny-slider-react";
import 'tiny-slider/dist/tiny-slider.css';


const SectionSlider = () => {

    const styles = {
        fontFamily: "sans-serif",
        textAlign: "center"
    };

    const imgStyles = {
        width: "100%",
        height: "320px",
        objectFit: "cover"
    };

    const imgs = [
        "https://i.imgur.com/ZbXgQLc.jpg",
        "data:image/jpeg;base64,",
        "https://ih1.redbubble.net/image.476420568.7763/scarf,x900-pad,750x1000,f8f8f8.jpg"
    ];

    const loadingImage =
        "data:image/gif;base64,\
        R0lGODlhAQABAPAAAMzMzAAAACH5BAAAAAAALAAAAAABAAEAAAICRAEAOw==";

    const settings = {
        lazyload: true,
        nav: false,
        mouseDrag: true,
        loop: true,
        items: 1,
        gutter: 5,
        responsive: {
            420: {
                items: 2
            }
        }
    };


    return (
        <>
            <TinySlider settings={settings}>
                {imgs.map((el, index) => (
                    <div key={index} style={{position: "relative"}}>
                        <img
                            className={`tns-lazy-img`}
                            src= {require({loadingImage})}
                            data-src={el}
                            alt=""
                            style={imgStyles}
                        />
                    </div>
                ))}
            </TinySlider>

        </>
    )


}

export default SectionSlider;
