import React from "react";
import '../scss/main.scss';
import TinySlider from "tiny-slider-react";
import 'tiny-slider/dist/tiny-slider.css';


const SectionSlider = () => {

    const settings = {
        lazyload: true,
        nav: false,
        mouseDrag: true
    };


    return (
        <>
            <TinySlider settings={settings}>
                {imgs.map((el, index) => (
                    <div key={index} style={{position: "relative"}}>
                        <img
                            className={`tns-lazy-img`}
                            src={loadingImage}
                            data-src={el}
                            alt=""
                            style={imgStyles}
                        />
                    </div>
                ))}
            </TinySlider>
            <button type="button" onClick={() => onGoTo('prev')}>Previous</button>
            <button type="button" onClick={() => onGoTo('next')}>Next</button>
        </>
    )


}

export default SectionSlider;
