import React, {useState,useEffect} from "react";
import '../scss/main.scss';

const SectionImage = ({frame1, frame2, frame3, frame4}) => {

    const img = [
        '../images/pexels-jhong-pascua-3018845.jpg',
        '../images/pexels-karolina-grabowska-4041392.jpg',
        '../images/pexels-marcelo-moreira-1926620.jpg'
    ]

    const [image, SetImage] = useState([])

    const [currentLight, setCurrentLight] = useState(1);
    const [timeCounter, setTimeCounter] = useState(0);

    useEffect(() => {
        const interval = setInterval(() => {
            setTimeCounter((prevState) => (prevState + 1));
        }, 1000);

        return () => {
            clearInterval(interval);
        };
    }, []);

    useEffect(() => {
        switch (currentLight) {
            default:
            case 1:
                if (timeCounter > frame1) {
                    setTimeCounter(0);
                    setCurrentLight(2);
                }
                break;
            case 2:
                if (timeCounter > frame2) {
                    setTimeCounter(0);
                    setCurrentLight(3);
                }
                break;
            case 3:
                if (timeCounter > frame3) {
                    setTimeCounter(0);
                    setCurrentLight(4);
                }
                break;
        }
    }, [timeCounter]);

    return (
        <section className='Home'>
            <img src={require('../images/pexels-jhong-pascua-3018845.jpg')}/>
        </section>
    )
}

export default SectionImage
